import java.util.Arrays;public class SumOfMultiples {    private int[] set;    private int mulMax;    private int total;    public SumOfMultiples(int mulMax, int[] set) {        this.set = set;        this.mulMax = mulMax;    }    public int getSum() {        for (int j = 1; j < mulMax; j++) {            if (isMultiple(j)) {                total += j;            }        }        return total;    }    private boolean isMultiple(int x) {        if (Arrays.stream(set).mapToDouble(i -> i).anyMatch(i -> (x / i) % 1 == 0)) return true;        return false;    }}